import React from 'react';
import '../App.css';

const MealPlanOptions = [
  {
    id: 1,
    day: 'Monday',
    meal: 'Breakfast',
    recipe: 'Oatmeal with fruits',
  },
  {
    id: 2,
    day: 'Monday',
    meal: 'Lunch',
    recipe: 'Grilled chicken with vegetables',
  },
  {
    id: 3,
    day: 'Monday',
    meal: 'Dinner',
    recipe: 'Salmon with quinoa and greens',
  },
  {
    id: 4,
    day: 'Tuesday',
    meal: 'Breakfast',
    recipe: 'Greek yogurt with berries',
  },
  {
    id: 5,
    day: 'Tuesday',
    meal: 'Lunch',
    recipe: 'Vegetable stir-fry with tofu',
  },
  {
    id: 6,
    day: 'Tuesday',
    meal: 'Dinner',
    recipe: 'Beef stew with brown rice',
  }, 
];

function MealPlan() {
    return (
      <div className="meal-plan">
        <h1>Weekly Meal Plan</h1>
        <div className="meal-plan-items">
          {Object.entries(MealPlanOptions).map(([day, meals]) => (
            <div key={day} className="meal-plan-day">
              <h2>{day}</h2>
              {MealPlanOptions.map((meal) => (
                <div key={meal.id} className="meal-plan-item">
                  <div className="text-content">
                    <h3>{meal.meal}</h3>
                    <p>{meal.recipe}</p>
                  </div>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    );
  }

  export default MealPlan;
